way2.swift<pre><code>import Foundation

for _ in 0...5 {
    let foodNames = ["🍔": "hamburger", "🍟": "french fries", "🌽": "popcorn"]

    //Map
    var mapped = foodNames.map({(key,value) -&gt; String in return value.capitalized})
    //Filter
    var filterred = mapped.filter({(food:String) -&gt; Bool in return !(food == "French Fries")})
    //Reduce
    var reduced = filterred.reduce("",{a, b in a +  " " + b})

    reduced.removeFirst()
    var outcome = reduced

    assert(
        outcome == "Hamburger Popcorn" ||
        outcome == "Popcorn Hamburger" 
    )
}
</code></pre>

