Basic code structure Basic<pre><code>class Solution {
private:
    vector&lt;int&gt; parents;
    int find(int x) {
        while (x != parents[x]) {
            parents[x] = parents[parents[x]];  // compression
            x = parents[x];
        }
        return x;
    }
    bool unions(int p, int q) {
        int x = find(p);
        int y = find(q);
        if (x != y) {
            parents[x] = y;
            return true;
        }
        return false;
    }
public:
    int countComponents(int n, vector&lt;pair&lt;int, int&gt;&gt;&amp; edges) {
        parents.resize(n);
        for (int i = 0; i &lt; n; i++) {
            parents[i] = i;
        }
        int count = n;
        for (const auto&amp; edge: edges) {
            if (unions(edge.first, edge.second)) {  //for each edges check if they are "unionable"
                --count;
            }
        }
        return count;
    }
};
</code></pre>

Simplified Solution solution.c++<pre><code>class Solution {
    int countComponents(int n, vector&lt;pair&lt;int, int&gt;&gt;&amp; edges) {
        vector&lt;int&gt; p(n);             //sets

        iota(begin(p), end(p), 0);    //indexing
        //equivalent sentence:
        //for (int i=0; i&lt;n; i++)
        //    p[i] = i;

        for (auto&amp; edge : edges) {
            int v = edge.first, w = edge.second;
            while (p[v] != v) v = p[v] = p[p[v]];  //find1
            while (p[w] != w) w = p[w] = p[p[w]];  //find2
            p[v] = w;                 //union
            n -= v != w;              
        }
        return n;
    }
};
</code></pre>

